import React , { useState , Fragment } from 'react';
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import CardModal from './CardModal';
import Card from './Card';
import styled from 'styled-components';
import { FlexWrapper, ThemesflatContainer, TitleH2 } from './styles';


const TodayPicksSection = styled.div`
    padding: 58px 0 43px;
    .sc-card-product {
        max-width: 324px;
        margin-right: 28px;
        margin-bottom: 30px;
        &:nth-child(4n) {
            margin-right: 0;
        }
    }
`;

const TodayPicksHeaderWrap = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 21px;
    a {
        text-decoration: none;
    }
`;

const ButtonWrap = styled.div`
    display: block;
    width: 100%;
    text-align: center;
    a {
        background: transparent none repeat scroll 0 0;
        border-color: #fff;
        border: 2px solid #eeecff;
        padding: 14px 40px;
        color: #fff;
        position: relative;
        border-radius: 30px;
        display: inline-block;
        transition: all 0.3s ease;
        text-decoration: none;
        &:hover {
            background-color: #fff;
            border-color: #fff;
            color: transparent;
        }
    }
`;


const TodayPicks = props => {
    const data = props.data;

    const [visible , setVisible] = useState(8);
    const showMoreItems = () => {
        setVisible((prevValue) => prevValue + 4);
    }
    const [modalShow, setModalShow] = useState(false);
    return (
        <Fragment>
        <TodayPicksSection className="tf-section today-pick">
            <ThemesflatContainer className="themesflat-container">
                    <TodayPicksHeaderWrap className="col-md-12">
                        <TitleH2 className="tf-title">
                            Today's Picks
                        </TitleH2>
                        <Link to="/explore-03" className="exp style2">EXPLORE MORE</Link>
                    </TodayPicksHeaderWrap>
                    <FlexWrapper>
                    {
                        data.slice(0,visible).map((item,index) => (
                            <Card item={item} key={index} className={`sc-card-product ${item.feature ? 'comingsoon' : '' } `}/>
                        ))
                    }
                    {
                        visible < data.length && 
                        <ButtonWrap className="col-md-12 wrap-inner load-more text-center"> 
                            <Link to="#" id="load-more" className="sc-button loadmore fl-button pri-3" onClick={showMoreItems}><span>Load More</span></Link>
                        </ButtonWrap>
                    }
                    </FlexWrapper>
            </ThemesflatContainer>
        </TodayPicksSection>
        <CardModal
        show={modalShow}
        onHide={() => setModalShow(false)}
         />
        </Fragment>
    );
}



TodayPicks.propTypes = {
    data: PropTypes.array.isRequired,
}


export default TodayPicks;
