import { useState ,useEffect } from 'react';
import { Link } from 'react-router-dom'
import logodark from '../../assets/images/shiba-inu-shib-logo.png';
import styled from 'styled-components';
import { ThemesflatContainer } from './styles';

const FooterInner = styled.div`
    padding: 80px 0 80px;
    background: #14141f;
    .sub-widget-logo {
        padding-right: 35px;
        margin-bottom: 26px;
        font-size: 14px;
        line-height: 22px;
    }
    p {
        color: white;
    }
    .widget-menu {
        padding-top: 18px;
        &.style-1 {
            padding-left: 30px;
        }
    }
`;

const ColLg3 = styled.div`
    flex: 0 0 25%;
    max-width: 25%;
`;

const LogoFooter = styled.div`
    margin-bottom: 13px;
    img {
        display: block;
        height: auto;
        max-width: 60px;
        vertical-align: middle;
    }
`;

const ColLg2 = styled.div`
    flex: 0 0 16.666667%;
    max-width: 16.666667%;
`;

const Footer = () => {
    const accountList = [
        {
            title: "Authors",
            link: "/authors-01"
        },
        {
            title: "Collection",
            link: "/wallet-connect"
        },
        {
            title: "Author Profile",
            link: "/edit-profile"
        },
        {
            title: "Create Item",
            link: "/create-item"
        },
    ]
    const resourcesList = [
        {
            title: "Help & Support",
            link: "/help-center"
        },
        {
            title: "Live Auctions",
            link: "/live-auctions"
        },
        {
            title: "Item Details",
            link: "/item-details-01"
        },
        {
            title: "Activity",
            link: "/activity-01"
        },
    ]
    const companyList = [
        {
            title: "Explore",
            link: "/explore-01"
        },
        {
            title: "Contact Us",
            link: "/contact-01"
        },
        {
            title: "Our Blog",
            link: "/blog"
        },
        {
            title: "FAQ",
            link: "/faq"
        },
    ]
    const socialList = [
        {
            icon: "fab fa-twitter",
            link: "#"
        },
        {
            icon: "fab fa-facebook",
            link: "#"
        },
        {
            icon: "fab fa-telegram-plane",
            link: "#"
        },
        {
            icon: "fab fa-youtube",
            link: "#"
        },
        {
            icon: "icon-fl-tik-tok-2",
            link: "#"
        },
        {
            icon: "icon-fl-vt",
            link: "#"
        },


    ]

    const [isVisible, setIsVisible] = useState(false);

    const scrollToTop = () => {
      window.scrollTo({
        top: 0,
        behavior: "smooth"
      });
    };

    useEffect(() => {
      const toggleVisibility = () => {
        if (window.pageYOffset > 500) {
          setIsVisible(true);
        } else {
          setIsVisible(false);
        }
      };

      window.addEventListener("scroll", toggleVisibility);

      return () => window.removeEventListener("scroll", toggleVisibility);
    }, []);


    return (
        <div>
            <FooterInner id="footer" className="footer-light-style clearfix bg-style">
                <ThemesflatContainer className="themesflat-container">
                    <ColLg3 className="col-lg-3 col-md-12 col-12">
                        <div className="widget widget-logo">
                            <LogoFooter className="logo-footer" id="logo-footer">
                                <Link to="/">
                                    <img className='logo-dark' id="logo_footer" src={logodark} alt="nft-gaming" />

                                </Link>
                            </LogoFooter>
                            <p className="sub-widget-logo">Lorem ipsum dolor sit amet,consectetur adipisicing elit. Quis non, fugit totam vel laboriosam vitae.</p>
                        </div>
                    </ColLg3>
                    <ColLg2 className="col-lg-2 col-md-4 col-sm-5 col-5">
                        <div className="widget widget-menu style-1">
                            <h5 className="title-widget">My Account</h5>
                            <ul>
                                {
                                    accountList.map((item,index) =>(
                                        <li key={index}><Link to={item.link}>{item.title}</Link></li>
                                    ))
                                }
                            </ul>
                        </div>
                    </ColLg2>
                    <ColLg2 className="col-lg-2 col-md-4 col-sm-7 col-7">
                        <div className="widget widget-menu style-2">
                            <h5 className="title-widget">Resources</h5>
                            <ul>
                                {
                                    resourcesList.map((item,index) =>(
                                        <li key={index}><Link to={item.link}>{item.title}</Link></li>
                                    ))
                                }
                            </ul>
                        </div>
                    </ColLg2>
                    <ColLg2 className="col-lg-2 col-md-4 col-sm-5 col-5">
                        <div className="widget widget-menu fl-st-3">
                            <h5 className="title-widget">Company</h5>
                            <ul>
                                {
                                    companyList.map((item,index) =>(
                                        <li key={index}><Link to={item.link}>{item.title}</Link></li>
                                    ))
                                }
                            </ul>
                        </div>
                    </ColLg2>
                    <ColLg3 className="col-lg-3 col-md-6 col-sm-7 col-12">
                        <div className="widget widget-subcribe">
                            <h5 className="title-widget">Subscribe Us</h5>
                            <div className="form-subcribe">
                                <form id="subscribe-form" action="#" method="GET" acceptCharset="utf-8" className="form-submit">
                                    <input name="email"  className="email" type="email" placeholder="info@yourgmail.com" required />
                                    <button id="submit" name="submit" type="submit"><i className="icon-fl-send"></i></button>
                                </form>
                            </div>
                            <div className="widget-social style-1 mg-t32">
                                <ul>
                                    {
                                        socialList.map((item,index) =>(
                                            <li key={index}><Link to={item.link}><i className={item.icon}></i></Link></li>
                                        ))
                                    }
                                </ul>
                            </div>
                        </div>
                    </ColLg3>
                </ThemesflatContainer>
            </FooterInner>
            {
                isVisible && 
                <Link onClick={scrollToTop}  to='#' id="scroll-top"></Link>
            }

        </div>

    );
}

export default Footer;
