import styled from 'styled-components';

const Container = styled.div`
    position: relative;
    margin-left: auto;
    margin-right: auto;
    padding: 0 15px;
    width: 1440px;
    max-width: 100%;
`;

const CardContainer = styled.div`
    width: 20%;
    margin-right: 60px;
    display: block;
    padding: 20px;
    img {
        max-width: 100%;
    }
`;

const FlexWrapper = styled.div`
    display: flex;
    flex-wrap: wrap;
    align-items: center;
`;

const MainInner = styled.div`
    background: #14141F;
`;

const ThemesflatContainer = styled.div`
    position: relative;
    margin-left: auto;
    margin-right: auto;
    padding-right: 15px;
    padding-left: 15px;
    width: 1440px;
    max-width: 100%;
`;

const FlexRow = styled.div`
    animation: move5 10s infinite linear;
    position: absolute;
    z-index: -1;
    right: 0%;
    top: 14%;
    max-width: 100%;
`;

const TitleH2 = styled.h2`
    font-size: 36px;
    position: relative;
    line-height: 44px;
    color: white;
    text-align: center;
`;

const Exp = styled.a`
    position: relative;
    font-weight: bold;
    font-size: 14px;
    line-height: 20px;
    letter-spacing: 0.1em;
    margin-top: -20px;
    background: linear-gradient(to right, #E250E5,#4B50E6,#E250E5);
    background-clip: text;
    -webkit-background-clip: text;
    font-weight: 700;
    background-size: 200% 200%;
    animation: rainbow 2s ease-in-out infinite;
    color: white;
    transition: color .2s ease-in-out;
`;

export { Container, CardContainer, FlexWrapper, MainInner, ThemesflatContainer, FlexRow, TitleH2, Exp };
